    private void btnDivisaoActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnDivisaoActionPerformed
        // TODO add your handling code here:
        int operator, res, division, rest;
        operator = Integer.parseInt(txtNumber1.getText());
        res = Integer.parseInt(txtNumber2.getText());
        
        division = (operator / res);
        rest = (operator % res);
        lblDiv.setText(Integer.toString(division));
        lblRes.setText(Integer.toString(rest));
        
    }//GEN-LAST:event_btnDivisaoActionPerformed

